var React = require('react');
var t = require('_i18n');
var storyStore = require('_stores/story');
var checkStory = require('_actions').checkStory;
var Story = require('./Story.jsx');

var Fanfiction = React.createClass({
    componentDidMount: function() {
        this.unsubscribe = storyStore.listen(this._onChange);
    },
    componentWillUnmount: function() {
        this.unsubscribe();
    },
    getInitialState: function() {
        return this._getStateFromStoryStore();
    },
    render: function() {
        return (
        <div className="main">
            <div>
                <label htmlFor="link">{t.gettext('URL')} : </label>
                <input placeholder={t.gettext('Insert link here ...')} id="link" onChange={this._handleInput} value={this.state.input} type="text"/>
                <button onClick={this._checkLink} disabled={!this.state.input || this.state.fetching}>{t.gettext('Check')}</button>
                <Story error={this.state.error} fetching={this.state.fetching} story={this.state.story}/>
            </div>
        </div>
        );
    },
    _getStateFromStoryStore: function() {
        return storyStore.getStatus();
    },
    _handleInput: function(e) {
        this.setState({input: e.target.value.trim()});
    },
    _checkLink: function(e) {
        checkStory(this.state.input);
    },
    _onChange: function(e) {
        this.setState(this._getStateFromStoryStore());
    }
});

module.exports = Fanfiction;
