var React = require('react');
var Link = require('react-router').Link;
var State = require('react-router').State;
var t = require('_i18n').t;

var Application = React.createClass({
    mixins: [State],
    render: function() {
        var link;
        if (this.isActive('config')) {
            link = <li className="navbar-choice"><Link to="application">{t.gettext('Back')}</Link></li>;
        } else {
            link = <li className="navbar-choice"><Link to="config">{t.gettext('Configuration')}</Link></li>;
        }
        return (
        <header className="navbar">
            <ul>
                {link}
            </ul>
        </header>
        );
    }
});

module.exports = Application;
